import React from 'react'
import {v4 as uuid} from 'uuid'
import { createContext,useState } from 'react'
//component names should always start with a capital l
//creating a context
export const FeedbackContext = createContext()
//we're gonna wrap our prop states components in this provider
export const FeedbackProvider = ({children})=>{
    const [feedback, setFeedback] = useState([
        {
            id : 1,
            rating: 10,
            text: "this is a text"
        },
        
        {
            id : 2,
            rating: 9,
            text: "this is a text"
        },
        {
            id : 3,
            rating: 8,
            text: "this is a text"
        },
        {
            id : 4,
            rating: 7,
            text: "this is a text"
        }
        
        
        
        ]
        
    )
        //to edit the card
    const[feedEdit, setFeedEdit] = useState({
        //item stores 
        item : {},
        edit: false

    })
    
    const handleadd = (newFeed) =>{
        newFeed.id= uuid()
        setFeedback([newFeed,...feedback])
    }
    const handledelete = (id)=> {
        if(window.confirm('delete this?')){
              setFeedback(feedback.filter((item)=>item.id!==id))
        }
     }
    return (
    <FeedbackContext.Provider value={{
        feedback, handledelete, handleadd
    }}>
    
    {children}
    
    </FeedbackContext.Provider>
    )
}
    
    


export default FeedbackProvider
