import React, { useState } from 'react'
import Card from './shared/Card'
import Button from './shared/Button'
import RatingList from './RatingList'
function FeedbackForm({handleadd}) {

    const[ratingbutn,setratingbtn] =useState(10)
    const [textinput, setText]= useState('')
    const[btnDisable,setbtnDisable]=useState(true)
    const[message,setmessage]=useState('')
    const handletext = (event)=>{
        if(textinput === ''){
            setbtnDisable(true)
            setmessage(null)

        }
        else if(textinput !== '' && textinput.trim().length<=10){
            //.trim is used to omit the white spaces so that it doesnt count it
            setmessage('text must be greater than 10')
            setbtnDisable(true)
        }
        else{
            setmessage(null)
            setbtnDisable(false)
        }

        setText(event.target.value)
    }

    const handlesubmit = (e)=>{
        //preventdefault stops the form from submitting so u can put your validations before
      e.preventDefault()  
      if(textinput.trim().length > 10){
        //make a new object and contain all the info of feedback in it
        const newFedd={
            textinput,
            ratingbutn
        }
        //adds the new info
        handleadd(newFedd)

        //clears the text
        setText('')
      }

    }

  


  return (
   <Card>
    <form onSubmit={handlesubmit}>
   <h2>How would you rate our services?</h2> 
   <RatingList select={(ratingbtn)=>setratingbtn(ratingbtn)} />
    <div className='input-group'>
    <input type ='text' placeholder='Write your review here...' value={textinput} onChange={handletext}/>
    <Button type='submit' isDisabled={btnDisable}>Send</Button>
    </div>
   <div className='message'>{message}</div>
    </form>
    </Card>

  )
}

export default FeedbackForm
